//Item variables to help condense code and reduce unnecessary duplication:
var hatOfWizardry = {
    eval : function(name) { // pass the unique name so the object entry will be unique
        CurrentSpells[name.toLowerCase()] = { // unique object name
            name: name + ' (item)',
            isHatOfWizardry: true, // custom attribute we can check against
            ability: "wizard",
            list: { 'class' : 'wizard', level : [0, 0] },
            known: { cantrips : 0, spells : 'list' },
            bonus: {
                bon1: {
                    name: 'Just select "Full List"',
                    spells: []
                },
                bon2: {
                    name: 'on the bottom left',
                    spells: []
                }
            },
            typeList: 4,
            refType: "item",
            allowUpCasting: true,
            firstCol: ""
        };
        SetStringifieds('spells'); CurrentUpdates.types.push('spells');
    },
    removeeval : function(name) {
        delete CurrentSpells[name.toLowerCase()];
        SetStringifieds('spells'); CurrentUpdates.types.push('spells');
    },
    calcChanges : {
        spellList: [
            function(spList, spName, spType) {
                // Remove the already known cantrips, from any source except magic items
                if (CurrentSpells[spName].isHatOfWizardry) { // check against the custom attribute
                    var allSpellsKnown = [];
                    for (var sCast in CurrentSpells) {
                        if (sCast.refType === "item") continue;
                        var oCast = CurrentSpells[sCast];
                        if (oCast.selectCa) allSpellsKnown = allSpellsKnown.concat(oCast.selectCa);
                        if (oCast.selectBo) allSpellsKnown = allSpellsKnown.concat(oCast.selectBo);
                    }
                    var knownCantrips = OrderSpells(allSpellsKnown, "single", false, false, 0);
                    if (!spList.notspells) spList.notspells = [];
                    spList.notspells = spList.notspells.concat(knownCantrips);
                }
            },
        ],
        spellAdd: [
            function (spellKey, spellObj, spName, isDuplicate) {
                if (CurrentSpells[spName].isHatOfWizardry) {
                    spellObj.firstCol = "";
                };
            }
        ]
    },
}

